CREATE OR REPLACE FUNCTION f_gap_fill_update(
    _tbl regclass, 
    _id text, 
    _row_num text, 
    OUT nullable_columns int, 
    OUT updated_rows int
) LANGUAGE plpgsql AS $func$
DECLARE 
    _pk text := quote_ident(_row_num); 
    _sql text;
BEGIN
    SELECT INTO _sql, nullable_columns concat_ws(E'
',
        'UPDATE ' || _tbl || ' t',
        'SET (' || string_agg(quote_ident(a.attname), ', ') || ') = ',
        '    (' || string_agg('u.' || quote_ident(a.attname), ', ') || ')',
        'FROM (',
        '   SELECT ' || _pk,
        '        , ' || string_agg(
                     format(
                         'COALESCE(%1$I, MAX(%1$I) OVER (PARTITION BY %2$s ORDER BY %3$s ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW)) AS %1$I',
                         a.attname, _id, _pk
                     ), 
                     ', '
                 ),
        '   FROM ' || _tbl,
        '   ) u',
        format('WHERE t.%1$s = u.%1$s', _pk),
        'AND (' || string_agg('t.' || quote_ident(a.attname), ', ') || ') IS DISTINCT FROM',
        '    (' || string_agg('u.' || quote_ident(a.attname), ', ') || ')'
    ), 
    count(*)
    FROM (
        SELECT a.attname 
        FROM pg_attribute a 
        WHERE a.attrelid = _tbl 
          AND a.attnum > 0 
          AND NOT a.attisdropped 
          AND NOT a.attnotnull 
        ORDER BY a.attnum
    ) a;

    RAISE NOTICE 'Generated SQL: %', _sql;

    IF nullable_columns = 0 THEN 
        RAISE EXCEPTION 'No nullable columns found in table >>%<<', _tbl; 
    ELSIF _sql IS NULL THEN 
        RAISE EXCEPTION 'Generated SQL is NULL. Check table or column logic!';
    END IF;

    EXECUTE _sql;

    GET DIAGNOSTICS updated_rows = ROW_COUNT; 
END $func$;

